CREATE TABLE coach(  id integer,  first_name character(40) NOT NULL,  last_name character(40) NOT NULL,  salary money,  CONSTRAINT pk_coach_id PRIMARY KEY (id),  CONSTRAINT positive_salary CHECK (salary > 0.0::money));CREATE TABLE sponsor(  id integer,  company_name character(40) NOT NULL,  budget money,  CONSTRAINT pk_sponsor_id PRIMARY KEY (id),  CONSTRAINT positive_budget CHECK (budget > 0.0::money));CREATE TABLE athlete(  id integer,  first_name character(40) NOT NULL,  last_name character(40) NOT NULL,  salary money,  finish_time time without time zone NOT NULL,  injury boolean NOT NULL DEFAULT FALSE,  coach_id integer NOT NULL,  sponsor_id integer NOT NULL,  CONSTRAINT pk_athlete_id PRIMARY KEY (id),  CONSTRAINT fk_athlete_coach FOREIGN KEY (coach_id)      REFERENCES coach (id) MATCH SIMPLE      ON UPDATE NO ACTION ON DELETE NO ACTION,  CONSTRAINT fk_athlete_sponsor FOREIGN KEY (sponsor_id)      REFERENCES sponsor (id) MATCH SIMPLE      ON UPDATE NO ACTION ON DELETE NO ACTION,  CONSTRAINT positive_salary CHECK (salary > 0.0::money));INSERT INTO coach VALUES (1, 'Garry', 'Vladimirov', '5000');INSERT INTO coach VALUES (2, 'Elena', 'Ivanova', '5500');INSERT INTO coach VALUES (3, 'John', 'White', '10000');INSERT INTO coach VALUES (4, 'Xen', 'Chu', '3000');INSERT INTO coach VALUES (5, 'Piotr', 'Kachowski', '7500');INSERT INTO sponsor VALUES (1, 'Adidas', '20000');INSERT INTO sponsor VALUES (2, 'Abibas', '40000');INSERT INTO sponsor VALUES (3, 'Romashka', '10000');INSERT INTO sponsor VALUES (4, 'Gazprom', '50000');INSERT INTO sponsor VALUES (5, 'BP', '30000');INSERT INTO athlete VALUES (1, 'Alex', 'Black', '1000', '00:25:41', false, 1, 1);INSERT INTO athlete VALUES (2, 'Marina', 'Marinina', '1500', '00:25:40', true, 2, 1);INSERT INTO athlete VALUES (3, 'Benjamin', 'Lucas', '2000', '00:20:20', false, 1, 2);INSERT INTO athlete VALUES (4, 'Leonardo', 'DiCaprio', '750', '00:15:17', true, 3, 2);INSERT INTO athlete V